using FHM.Models;
using FHM.Models.FormatModels;
using FHM.Models.GameModel;
using FHM.Models.TournamentModels;
using Microsoft.AspNetCore.Identity;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace FHM.Data
{
    public static class DbInitializer
    {
        public static void Initialize(ApplicationDbContext context, RoleManager<AppRole> roleManager,
            UserManager<ApplicationUser> userManager)
        {
            context.Database.EnsureCreated();

            if (!roleManager.RoleExistsAsync("Customer").Result)
            {
                AppRole role = new AppRole();
                role.Name = "Customer";
                IdentityResult roleResult = roleManager.CreateAsync(role).Result;
            }
            if (!roleManager.RoleExistsAsync("Organizer").Result)
            {
                AppRole role = new AppRole();
                role.Name = "Organizer";
                IdentityResult roleResult = roleManager.CreateAsync(role).Result;
            }
            if (!roleManager.RoleExistsAsync("Employee").Result)
            {
                AppRole role = new AppRole();
                role.Name = "Employee";
                IdentityResult roleResult = roleManager.CreateAsync(role).Result;
            }
            if (!roleManager.RoleExistsAsync("Admin").Result)
            {
                AppRole role = new AppRole();
                role.Name = "Admin";
                IdentityResult roleResult = roleManager.CreateAsync(role).Result;
            }

            if (userManager.FindByNameAsync("looman.dane@gmail.com").Result == null)
            {
                ApplicationUser user = new ApplicationUser();
                user.UserName = "looman.dane@gmail.com";
                user.Email = "looman.dane@gmail.com";
                user.FirstName = "Dane";
                user.LastName = "Looman";
                user.Id = "User1";
                user.EmailConfirmed = true;

                IdentityResult result = userManager.CreateAsync(user, "Password1234!").Result;
                if (result.Succeeded)
                {
                    userManager.AddToRoleAsync(user,
                                        "Admin").Wait();
                }
            }

            if (!context.Games.Any())
            {
                var games = new Game[]
                {
                    new Game {GameName = "Magic The Gathering", GameShortDescription = "Classic Strategy Trading Card Game.", GameLongDescription = "Magic: The Gathering is both a trading card and digital collectible card game created by Richard Garfield. Released in 1993 by Wizards of the Coast, Magic was the first trading card game created and it continues to thrive, with approximately twenty million players as of 2015,and over twenty billion Magic cards produced in the period of 2008 to 2016 alone.Each game represents a battle between wizards known as planeswalkers, who employ spells, artifacts, and creatures depicted on individual Magic cards to defeat their opponents. Although the original concept of the game drew heavily from the motifs of traditional fantasy role-playing games such as Dungeons & Dragons, the gameplay of Magic bears little similarity to pencil-and-paper adventure games, while having substantially more cards and more complex rules than many other card games.", GameImageUrl = "https://227rsi2stdr53e3wto2skssd7xe-wpengine.netdna-ssl.com/wp-content/uploads/2017/01/mtg-banner-730x280.jpg", GameIsGameOfTheWeek = true, GameImageThumbnailURL = "data:image/jpeg;base64," },
                    new Game {GameName = "Pokemon", GameShortDescription = "Collectible card game based on Nintendo's Pokémon franchise of video games and anime", GameLongDescription = "Collectible card game based on Nintendo's Pokémon franchise of video games and anime.Players assume the role of a Pokémon trainer and use their Pokémon to battle their opponents'. Players play Pokémon to the field and attack their opponent's Pokémon. A Pokémon that has sustained enough damage is knocked out, and the player who knocked it out draws a Prize card. There are usually six Prize cards, and the primary win condition is to draw all of them. Other ways to win are by knocking out all the Pokémon the opponent has on the field such that the opponent has none left, or if at the beginning of their opponent's turn there are no cards left to draw in the opponent's deck.", GameImageUrl = "data:image/png;base64,", GameIsGameOfTheWeek = false, GameImageThumbnailURL = "data:image/png;base64," },
                };

                foreach (Game g in games)
                {
                    context.Games.Add(g);
                }
                context.SaveChanges();
            }
            if (!context.Formats.Any())
            {
                var formats = new Format[]
                {
                new Format {FormatName = "Standard", FormatDescription="Last two years of magic starting in Sept.", FormatLink="https://magic.wizards.com/en/content/standard-formats-magic-gathering", GameID=1},
                new Format {FormatName = "Modern", FormatDescription="Last ten years of magic", FormatLink="https://magic.wizards.com/en/game-info/gameplay/formats/modern", GameID=1},
                new Format {FormatName = "Standard", FormatDescription="Last two years of Pokemon starting in July", FormatLink="https://www.pokemon.com/us/pokemon-news/2019-season-pokemon-tcg-format-rotation/", GameID=2}
                };

                foreach (Format f in formats)
                {
                    context.Formats.Add(f);
                }
                context.SaveChanges();
            }
            if (!context.Tournaments.Any())
            {
                var tournaments = new Tournament[]
                {
                new Tournament {TournamentName = "Friday Night Magic",
                    TournamentDescription ="Player Magic Every Friday",
                    GameID = 1,
                    TournamentFee = 4.95M,
                    TournamentStartTime = new DateTime(2020, 1, 1, 18, 30, 0),
                    TournamentGame = context.Games.Where(p => p.GameID == 1).FirstOrDefault(),
                    TournamentFormat = context.Formats.Where(p => p.FormatID == 1).FirstOrDefault(),
                },
                };

                foreach (Tournament t in tournaments)
                {
                    context.Tournaments.Add(t);
                }
                context.SaveChanges();
            }
        }
    }
}
